window.addEventListener("DOMContentLoaded",function(){var form=document.getElementById("contact-form");var button=document.getElementById("contact-form-button");var status=document.getElementById("contact-form-status");var napis=document.getElementById("napis")
function success(){form.reset();button.style="display: none ";status.innerHTML="Thanks! Contact form is submitted successfully.";}
function error(){status.innerHTML="Dziękujemy - formularz został wysłany - wkrótce skontaktujemy się z Państwem";napis.style="display: none";form.reset()}
form.addEventListener("submit",function(ev){ev.preventDefault();var data=new FormData(form);console.log(data);ajax(form.method,form.action,data,success,error);});});function ajax(method,url,data,success,error){var xhr=new XMLHttpRequest();xhr.open('POST','https://docs.google.com/forms/d/1Wl7XFGU_0Qj9FcyQ5jW1F8ynwxliB08RYxA49n9prSE/formResponse');xhr.onreadystatechange=function(){if(xhr.readyState!==XMLHttpRequest.DONE)return;if(xhr.status===200){success(xhr.response,xhr.responseType);}else{error(xhr.status,xhr.response,xhr.responseType);}};xhr.send(data);}